        <!-- Roll Button -->
        <button class="roll-button" :disabled="isRolling" @click="rollDice">
          <span v-if="isRolling">üé≤ W√ºrfelt...</span>
          <span v-else>üé≤ W√úRFELN</span>
        </button>
