<script setup lang="ts">
import {ref, computed, onUnmounted} from 'vue';

// Game state
const isRolling = ref(false);
const showResult = ref(false);
const currentFace = ref(1);
const isJackpot = ref(false);
const lastRolledFace = ref(0); // Speichert den letzten gew√ºrfelten Begriff

// Timer state
const timerOptions = [10, 15, 30, 45, 60];
const timerDuration = ref(30);
const timeLeft = ref(timerDuration.value);
const timerActive = ref(false);
const isSpeedRound = ref(false);
let timerInterval: number | null = null;

// Categories
const categories = [
  {name: 'STADT', description: 'Nenne eine Stadt (z.B. Berlin)', icon: 'üèôÔ∏è'},
  {name: 'FLUSS', description: 'Nenne einen Fluss (z.B. Rhein)', icon: 'üåä'},
  {name: 'LAND', description: 'Nenne ein Land (z.B. Frankreich)', icon: 'üåç'},
  {name: 'TIER', description: 'Nenne ein Tier (z.B. Elefant)', icon: 'üêæ'},
  {name: 'NAME', description: 'Nenne einen Vornamen (z.B. Anna)', icon: 'üë§'},

  {
    name: 'JACKPOT',
    description: 'W√§hle KATEGORIE & BUCHSTABEN frei!',
    icon: 'üé∞',
  },
];

// Computed properties
const resultCategory = computed(() => {
  if (currentFace.value === 6) return 'üé∞ JACKPOT üé∞';
  return categories[currentFace.value - 1]?.name || '';
});

const resultDescription = computed(() => {
  if (currentFace.value === 6) return 'W√§hle KATEGORIE & BUCHSTABEN frei!';
  return categories[currentFace.value - 1]?.description || '';
});

const resultIcon = computed(() => {
  return categories[currentFace.value - 1]?.icon || 'üé≤';
});

const currentLetter = computed(() => {
  if (currentFace.value === 6) {
    const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    return letters[Math.floor(Math.random() * 26)];
  }
  return '';
});

const diceStyle = computed(() => {
  const rotations = {
    1: {x: 0, y: 0, z: 0},
    2: {x: 0, y: 90, z: 0},
    3: {x: 0, y: -90, z: 0},
    4: {x: 90, y: 0, z: 0},
    5: {x: -90, y: 0, z: 0},
    6: {x: 0, y: 180, z: 0},
  };

  const rotation = rotations[currentFace.value as keyof typeof rotations];
  return {
    transform: `rotateX(${rotation.x}deg) rotateY(${rotation.y}deg) rotateZ(${rotation.z}deg)`,
    animationName: isRolling.value ? 'roll' : 'none',
    animationDuration: isRolling.value ? '0.8s' : '0s',
    animationTimingFunction: 'ease-out',
    animationFillMode: 'forwards',
    animationDelay: '0s',
    animationIterationCount: '1',
    animationDirection: 'normal',
    animationPlayState: isRolling.value ? 'running' : 'paused',
  };
});

const timerProgressStyle = computed(() => {
  const percent = (timeLeft.value / timerDuration.value) * 100;
  let bg = '#16a34a';
  if (timeLeft.value <= 10) bg = '#ef4444';
  else if (timeLeft.value <= 20) bg = '#fde047';
  return {
    width: percent + '%',
    background: bg,
  };
});

// Timer functions
function setTimerDuration(duration: number) {
  timerDuration.value = duration;
  timeLeft.value = duration;
  isSpeedRound.value = duration <= 15;
}

function toggleTimer() {
  // Initialize audio context on first user interaction (iOS requirement)
  initAudioContext();

  if (timerActive.value) {
    stopTimer();
  } else {
    startTimer();
  }
}

function startTimer() {
  if (timerActive.value) return;

  timerActive.value = true;
  timeLeft.value = timerDuration.value;

  timerInterval = window.setInterval(() => {
    timeLeft.value--;

    if (timeLeft.value <= 0) {
      stopTimer();
      playSound('timer');
      alert('Zeit abgelaufen!');
    }
  }, 1000);
}

function stopTimer() {
  if (timerInterval) {
    clearInterval(timerInterval);
    timerInterval = null;
  }
  timerActive.value = false;
}

function resetTimer() {
  stopTimer();
  timeLeft.value = timerDuration.value;
  startTimer();
}

function resetGame() {
  lastRolledFace.value = 0; // Reset last rolled face
  currentFace.value = 1;
  showResult.value = false;
  isJackpot.value = false;
  isRolling.value = false;
}

function formatTime(seconds: number): string {
  const mins = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${mins}:${secs.toString().padStart(2, '0')}`;
}

// Audio context for iOS compatibility
let audioContext: AudioContext | null = null;

// Initialize audio context on first user interaction
function initAudioContext() {
  if (!audioContext) {
    audioContext = new (window.AudioContext ||
      (window as any).webkitAudioContext)();
  }
  // Resume if suspended (iOS requirement)
  if (audioContext.state === 'suspended') {
    audioContext.resume();
  }
}

// Sound function
function playSound(type: 'roll' | 'success' | 'jackpot' | 'timer') {
  try {
    // Initialize audio context if not exists
    initAudioContext();

    if (!audioContext) return;

    switch (type) {
      case 'roll':
        // Sch√∂ner W√ºrfel-Sound mit mehreren harmonischen T√∂nen
        const rollDuration = 0.8;
        const rollGain = audioContext.createGain();
        rollGain.connect(audioContext.destination);
        rollGain.gain.setValueAtTime(0.15, audioContext.currentTime);
        rollGain.gain.exponentialRampToValueAtTime(
          0.001,
          audioContext.currentTime + rollDuration,
        );

        // Hauptton (sanfter Triangle-Wave)
        const mainOsc = audioContext.createOscillator();
        mainOsc.type = 'triangle';
        mainOsc.frequency.setValueAtTime(220, audioContext.currentTime);
        mainOsc.frequency.exponentialRampToValueAtTime(
          110,
          audioContext.currentTime + rollDuration,
        );
        mainOsc.connect(rollGain);
        mainOsc.start();
        mainOsc.stop(audioContext.currentTime + rollDuration);

        // Harmonie-Ton (h√∂her)
        const harmonyOsc = audioContext.createOscillator();
        harmonyOsc.type = 'triangle';
        harmonyOsc.frequency.setValueAtTime(330, audioContext.currentTime);
        harmonyOsc.frequency.exponentialRampToValueAtTime(
          165,
          audioContext.currentTime + rollDuration,
        );
        harmonyOsc.connect(rollGain);
        harmonyOsc.start();
        harmonyOsc.stop(audioContext.currentTime + rollDuration);

        // Bass-Ton (tiefer)
        const bassOsc = audioContext.createOscillator();
        bassOsc.type = 'triangle';
        bassOsc.frequency.setValueAtTime(110, audioContext.currentTime);
        bassOsc.frequency.exponentialRampToValueAtTime(
          55,
          audioContext.currentTime + rollDuration,
        );
        bassOsc.connect(rollGain);
        bassOsc.start();
        bassOsc.stop(audioContext.currentTime + rollDuration);
        break;

      case 'success':
        // Angenehmer Erfolgs-Sound mit aufsteigender Melodie
        const successGain = audioContext.createGain();
        successGain.connect(audioContext.destination);
        successGain.gain.setValueAtTime(0.2, audioContext.currentTime);
        successGain.gain.exponentialRampToValueAtTime(
          0.001,
          audioContext.currentTime + 0.6,
        );

        // Aufsteigende T√∂ne: C, E, G
        const tones = [523.25, 659.25, 783.99]; // C5, E5, G5
        tones.forEach((freq, index) => {
          const osc = audioContext.createOscillator();
          osc.type = 'sine';
          osc.frequency.setValueAtTime(
            freq,
            audioContext.currentTime + index * 0.1,
          );
          osc.connect(successGain);
          osc.start(audioContext.currentTime + index * 0.1);
          osc.stop(audioContext.currentTime + index * 0.1 + 0.3);
        });
        break;

      case 'jackpot':
        // Spektakul√§rer Jackpot-Sound mit Glocken-Effekt
        const jackpotGain = audioContext.createGain();
        jackpotGain.connect(audioContext.destination);
        jackpotGain.gain.setValueAtTime(0.25, audioContext.currentTime);
        jackpotGain.gain.exponentialRampToValueAtTime(
          0.001,
          audioContext.currentTime + 1.2,
        );

        // Glocken-√§hnliche T√∂ne
        const jackpotTones = [392.0, 523.25, 659.25, 830.61, 1046.5]; // G4, C5, E5, G#5, C6
        jackpotTones.forEach((freq, index) => {
          const osc = audioContext.createOscillator();
          osc.type = 'sine';
          osc.frequency.setValueAtTime(
            freq,
            audioContext.currentTime + index * 0.15,
          );

          // Glocken-Effekt mit schneller Abnahme
          const oscGain = audioContext.createGain();
          oscGain.gain.setValueAtTime(
            0.3,
            audioContext.currentTime + index * 0.15,
          );
          oscGain.gain.exponentialRampToValueAtTime(
            0.001,
            audioContext.currentTime + index * 0.15 + 0.4,
          );

          osc.connect(oscGain);
          oscGain.connect(jackpotGain);
          osc.start(audioContext.currentTime + index * 0.15);
          osc.stop(audioContext.currentTime + index * 0.15 + 0.4);
        });
        break;

      case 'timer':
        // Sanfter Timer-Sound
        const timerOsc = audioContext.createOscillator();
        const timerGain = audioContext.createGain();
        timerOsc.type = 'sine';
        timerOsc.frequency.setValueAtTime(440, audioContext.currentTime);
        timerGain.gain.setValueAtTime(0.15, audioContext.currentTime);
        timerGain.gain.exponentialRampToValueAtTime(
          0.001,
          audioContext.currentTime + 0.2,
        );
        timerOsc.connect(timerGain);
        timerGain.connect(audioContext.destination);
        timerOsc.start();
        timerOsc.stop(audioContext.currentTime + 0.2);
        break;
    }
  } catch (error) {
    console.log('Audio not supported');
  }
}

// Functions
function rollDice() {
  if (isRolling.value) return;

  // Initialize audio context on first user interaction (iOS requirement)
  initAudioContext();

  isRolling.value = true;
  showResult.value = false;
  isJackpot.value = false;

  // Play roll sound
  playSound('roll');

  // Random result - ensure it's different from the last roll
  let result;
  if (lastRolledFace.value === 0) {
    // Erster Wurf - kann alles sein
    result = Math.floor(Math.random() * 6) + 1;
  } else {
    // Folge-W√ºrfe - muss anders sein als der letzte
    do {
      result = Math.floor(Math.random() * 6) + 1;
    } while (result === lastRolledFace.value);
  }

  currentFace.value = result;
  lastRolledFace.value = result; // Speichere den aktuellen Wurf

  // Log f√ºr Debugging
  console.log(
    `Gew√ºrfelt: ${result} (${
      categories[result - 1]?.name || 'Unbekannt'
    }), Letzter: ${lastRolledFace.value} (${
      lastRolledFace.value > 0
        ? categories[lastRolledFace.value - 1]?.name || 'Unbekannt'
        : 'Erster Wurf'
    })`,
  );

  // Check if jackpot
  if (result === 6) {
    isJackpot.value = true;
  }

  // Show result after animation - but stop rolling immediately when animation ends
  setTimeout(() => {
    isRolling.value = false;
    showResult.value = true;
    playSound('success');
  }, 800); // Reduced from 1000ms to 800ms for better responsiveness

  // Force stop animation after 800ms to prevent glitching
  setTimeout(() => {
    if (!isRolling.value) {
      // Force the dice to stop at the final position
      const diceElement = document.querySelector('.dice') as HTMLElement;
      if (diceElement) {
        const rotations = {
          1: {x: 0, y: 0, z: 0},
          2: {x: 0, y: 90, z: 0},
          3: {x: 0, y: -90, z: 0},
          4: {x: 90, y: 0, z: 0},
          5: {x: -90, y: 0, z: 0},
          6: {x: 0, y: 180, z: 0},
        };

        // Immediately stop all animations
        diceElement.style.animation = 'none';
        diceElement.style.animationName = 'none';
        diceElement.style.animationDuration = '0s';
        diceElement.style.animationPlayState = 'paused';

        // Set final position
        diceElement.style.transform = `rotateX(${
          rotations[currentFace.value as keyof typeof rotations].x
        }deg) rotateY(${
          rotations[currentFace.value as keyof typeof rotations].y
        }deg) rotateZ(${
          rotations[currentFace.value as keyof typeof rotations].z
        }deg)`;

        // Force a reflow to ensure the animation stops
        diceElement.offsetHeight;

        // Remove any remaining animation styles
        diceElement.style.removeProperty('animation-name');
        diceElement.style.removeProperty('animation-duration');
        diceElement.style.removeProperty('animation-timing-function');
        diceElement.style.removeProperty('animation-fill-mode');
        diceElement.style.removeProperty('animation-delay');
        diceElement.style.removeProperty('animation-iteration-count');
        diceElement.style.removeProperty('animation-direction');
        diceElement.style.removeProperty('animation-play-state');

        // Add a small delay to ensure the browser processes the changes
        requestAnimationFrame(() => {
          diceElement.style.animation = 'none';
          diceElement.style.transform = `rotateX(${
            rotations[currentFace.value as keyof typeof rotations].x
          }deg) rotateY(${
            rotations[currentFace.value as keyof typeof rotations].y
          }deg) rotateZ(${
            rotations[currentFace.value as keyof typeof rotations].z
          }deg)`;
        });
      }
    }
  }, 800);
}

// Cleanup on unmount
onUnmounted(() => {
  if (timerInterval) {
    clearInterval(timerInterval);
  }
});
</script>

